import React from 'react';
import { useExperiment } from '../context/ExperimentContext';

const GroundTruthFloorPlan = () => {
  const { targetAreas, lightsToTurnOff, roomLayout, floorPlanOrientation, lightStates, currentCondition } = useExperiment();

  // Use room layout if available, otherwise default to 4 rooms
  const rooms = roomLayout?.rooms || [
    { id: 'A', left: 0, top: 0, width: 400, height: 300 },
    { id: 'B', left: 400, top: 0, width: 400, height: 300 },
    { id: 'C', left: 0, top: 300, width: 533, height: 300 },
    { id: 'D', left: 533, top: 300, width: 267, height: 300 }
  ];

  const taskType = currentCondition?.taskType || 'turnOn2';

  return (
    <div className="w-full">
      {/* Task Instructions */}
      <div className="mb-4 text-center">
        {taskType === 'turnOn2' ? (
          <>
            <p className="text-lg font-semibold text-gray-700 mb-2">
              Task: Turn ON 2 lights
            </p>
            <p className="text-sm text-gray-600">
              Turn ON lights for areas: <span className="font-bold text-yellow-600">{targetAreas.length > 0 ? targetAreas.join(', ') : 'None'}</span>
            </p>
          </>
        ) : taskType === 'turnOn1Off1' ? (
          <>
            <p className="text-lg font-semibold text-gray-700 mb-2">
              Task: Turn ON 1 light, Turn OFF 1 light
            </p>
            <p className="text-sm text-gray-600">
              Turn ON: <span className="font-bold text-yellow-600">{targetAreas.length > 0 ? targetAreas.join(', ') : 'None'}</span>
              {lightsToTurnOff.length > 0 && (
                <> | Turn OFF: <span className="font-bold text-red-600">{lightsToTurnOff.join(', ')}</span></>
              )}
            </p>
          </>
        ) : (
          <>
            <p className="text-lg font-semibold text-gray-700 mb-2">
              Target Areas: <span className="text-yellow-600 font-bold">{targetAreas.length > 0 ? targetAreas.join(', ') : 'None'}</span>
            </p>
            <p className="text-sm text-gray-600">
              Turn ON the lights for these areas
            </p>
          </>
        )}
      </div>

      {/* Floor Plan Container - with rotation */}
      {/* Container needs to be large enough to accommodate rotated floor plan */}
      <div className="max-w-5xl mx-auto" style={{ 
        minHeight: '800px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '20px'
      }}>
        <div 
          className="relative border-4 border-gray-800 bg-white rounded-lg shadow-lg"
          style={{ 
            width: '800px',
            height: '600px',
            maxWidth: 'calc(100% - 40px)',
            maxHeight: 'calc(100% - 40px)',
            transform: `rotate(${floorPlanOrientation || 0}deg)`,
            transformOrigin: 'center center',
            backgroundImage: 'repeating-linear-gradient(0deg, transparent, transparent 19px, #e5e7eb 19px, #e5e7eb 20px), repeating-linear-gradient(90deg, transparent, transparent 19px, #e5e7eb 19px, #e5e7eb 20px)',
            backgroundSize: '20px 20px'
          }}
        >
          {/* Render rooms from layout */}
          {rooms.map((room, index) => {
            const isOn = lightStates[room.id] === true; // Show yellow when switch is turned on

            // Check if there's a room to the right or below
            const hasRoomRight = rooms.some(r => 
              Math.abs(r.top - room.top) < 5 && 
              Math.abs(r.left - (room.left + room.width)) < 5
            );
            const hasRoomBelow = rooms.some(r => 
              Math.abs(r.left - room.left) < 5 && 
              Math.abs(r.top - (room.top + room.height)) < 5
            );

            return (
              <div
                key={room.id}
                className="absolute transition-all duration-300"
                style={{
                  left: `${(room.left / 800) * 100}%`,
                  top: `${(room.top / 600) * 100}%`,
                  width: `${(room.width / 800) * 100}%`,
                  height: `${(room.height / 600) * 100}%`,
                  borderRight: hasRoomRight ? '2px solid #9ca3af' : 'none',
                  borderBottom: hasRoomBelow ? '2px solid #9ca3af' : 'none'
                }}
              >
                <div className="w-full h-full flex items-center justify-center relative">
                  {/* Letter label without circle - yellow background when light is on */}
                  <div
                    className={`text-3xl font-bold transition-all duration-300 ${
                      isOn
                        ? 'text-yellow-600'
                        : 'text-gray-800'
                    }`}
                  >
                    {room.id}
                  </div>
                  {/* Highlight overlay when light is on */}
                  {isOn && (
                    <div className="absolute inset-0 bg-yellow-200/40 rounded-lg pointer-events-none border-2 border-yellow-500"></div>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Legend */}
      <div className="mt-6 flex justify-center gap-6 text-sm flex-wrap">
        <div className="flex items-center gap-2">
          <div className="w-5 h-5 rounded-full border-4 border-yellow-500 bg-yellow-200"></div>
          <span className="text-gray-700 font-semibold">Light ON</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-5 h-5 rounded-full border-4 border-black bg-white"></div>
          <span className="text-gray-700">Light OFF</span>
        </div>
      </div>

      {/* Orientation indicator */}
      {floorPlanOrientation && floorPlanOrientation !== 0 && (
        <div className="mt-4 text-center text-sm text-gray-600">
          Orientation: {floorPlanOrientation}Â°
        </div>
      )}
    </div>
  );
};

export default GroundTruthFloorPlan;
