/**
 * Latin Square counterbalancing for 6 groups and 6 conditions
 * 
 * Group → Condition Order:
 * Group A: 1,2,3,4,5,6
 * Group B: 2,3,4,5,6,1
 * Group C: 3,4,5,6,1,2
 * Group D: 4,5,6,1,2,3
 * Group E: 5,6,1,2,3,4
 * Group F: 6,1,2,3,4,5
 */

/**
 * Get the condition order for a given group
 * @param {string} group - Group letter (A, B, C, D, E, or F)
 * @returns {Array<number>} - Array of condition indices in order (0-5)
 */
export const getConditionOrderForGroup = (group) => {
  const groupUpper = group.toUpperCase().trim();

  // Map groups to starting position (0-indexed)
  const groupStartMap = {
    'A': 0,  // Starts at condition 1 (index 0)
    'B': 1,  // Starts at condition 2 (index 1)
    'C': 2,  // Starts at condition 3 (index 2)
    'D': 3,  // Starts at condition 4 (index 3)
    'E': 4,  // Starts at condition 5 (index 4)
    'F': 5   // Starts at condition 6 (index 5)
  };

  const startIndex = groupStartMap[groupUpper] || 0;

  // Generate order: start, start+1, ..., 5, 0, 1, ..., start-1
  const order = [];
  for (let i = 0; i < 6; i++) {
    order.push((startIndex + i) % 6);
  }

  return order;
};
