import React, { useState, useEffect } from 'react';
import RoomLayout from './components/RoomLayout';
import './App.css';

function App() {
  const [rooms, setRooms] = useState([]);
  const [switchStates, setSwitchStates] = useState({});

  // Generate random number of rooms (4-8) and create room layout
  useEffect(() => {
    generateNewLayout();
  }, []);

  const generateNewLayout = () => {
    const numRooms = Math.floor(Math.random() * 5) + 4; // 4-8 rooms
    const newRooms = [];
    const newSwitchStates = {};

    // Container dimensions
    const containerWidth = 800;
    const containerHeight = 600;

    // Generate rooms that fill the space without overlapping
    const rooms = generateFloorPlan(numRooms, containerWidth, containerHeight);

    rooms.forEach((room, i) => {
      const roomId = String.fromCharCode(65 + i); // A, B, C, D, etc.
      newRooms.push({
        id: roomId,
        label: roomId,
        isActive: false,
        width: room.width,
        height: room.height,
        left: room.left,
        top: room.top
      });
      newSwitchStates[roomId] = false;
    });

    setRooms(newRooms);
    setSwitchStates(newSwitchStates);
  };

  // Function to generate a floor plan that fills the space without overlaps
  const generateFloorPlan = (numRooms, containerWidth, containerHeight) => {
    const rooms = [];
    const minRoomSize = 80;
    const maxRoomSize = 200;

    // Try different approaches based on room count
    if (numRooms <= 4) {
      // Simple 2x2 grid with some variation
      const cellWidth = containerWidth / 2;
      const cellHeight = containerHeight / 2;

      for (let i = 0; i < numRooms; i++) {
        const row = Math.floor(i / 2);
        const col = i % 2;

        const width = cellWidth - 4;
        const height = cellHeight - 4;
        const left = col * cellWidth + 2;
        const top = row * cellHeight + 2;

        rooms.push({ width, height, left, top });
      }
    } else if (numRooms <= 6) {
      // 3x2 grid
      const cellWidth = containerWidth / 3;
      const cellHeight = containerHeight / 2;

      for (let i = 0; i < numRooms; i++) {
        const row = Math.floor(i / 3);
        const col = i % 3;

        const width = cellWidth - 4;
        const height = cellHeight - 4;
        const left = col * cellWidth + 2;
        const top = row * cellHeight + 2;

        rooms.push({ width, height, left, top });
      }
    } else {
      // 4x2 grid
      const cellWidth = containerWidth / 4;
      const cellHeight = containerHeight / 2;

      for (let i = 0; i < numRooms; i++) {
        const row = Math.floor(i / 4);
        const col = i % 4;

        const width = cellWidth - 4;
        const height = cellHeight - 4;
        const left = col * cellWidth + 2;
        const top = row * cellHeight + 2;

        rooms.push({ width, height, left, top });
      }
    }

    return rooms;
  };

  const toggleSwitch = (roomId) => {
    setSwitchStates(prev => ({
      ...prev,
      [roomId]: !prev[roomId]
    }));
  };

  return (
    <div className="app">
      <div className="app-header">
        <h1>Virtual Room Mock-up</h1>
        <button onClick={generateNewLayout} className="regenerate-btn">
          Generate New Layout
        </button>
      </div>

      <div className="app-content">
        <div className="layout-section">
          <h2>Floor Plan Layout</h2>
          <RoomLayout 
            rooms={rooms} 
            switchStates={switchStates}
            onToggleSwitch={toggleSwitch}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
