import React, { useRef, useState, useEffect } from 'react';
import * as THREE from 'three';
import { STLLoader } from 'three/examples/jsm/loaders/STLLoader';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import './STLViewer.css';

function STLViewer({ stlFile, isVisible }) {
  const mountRef = useRef(null);
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);
  const cameraRef = useRef(null);
  const controlsRef = useRef(null);
  const meshRef = useRef(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!mountRef.current) return;

    // Initialize Three.js scene
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xf0f0f0);
    sceneRef.current = scene;

    // Camera
    const camera = new THREE.PerspectiveCamera(75, 400 / 400, 0.1, 1000);
    camera.position.set(5, 5, 5);
    camera.lookAt(0, 0, 0);
    cameraRef.current = camera;

    // Renderer
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(400, 400);
    renderer.shadowMap.enabled = true;
    renderer.shadowMap.type = THREE.PCFSoftShadowMap;
    mountRef.current.appendChild(renderer.domElement);
    rendererRef.current = renderer;

    // Lighting
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.8);
    scene.add(ambientLight);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 1.0);
    directionalLight.position.set(10, 10, 5);
    directionalLight.castShadow = true;
    scene.add(directionalLight);

    const pointLight = new THREE.PointLight(0xffffff, 0.8);
    pointLight.position.set(-10, -10, -5);
    scene.add(pointLight);

    // Add a hemisphere light for better overall illumination
    const hemisphereLight = new THREE.HemisphereLight(0xffffbb, 0x080820, 0.5);
    scene.add(hemisphereLight);


    // Controls
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.05;
    controls.enableZoom = true;
    controls.enablePan = true;
    controlsRef.current = controls;

    // Animation loop
    console.log('Starting animation loop...');
    const animate = () => {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    };
    animate();
    console.log('Animation loop started');

    // Cleanup
    return () => {
      if (mountRef.current && renderer.domElement) {
        mountRef.current.removeChild(renderer.domElement);
      }
      renderer.dispose();
    };
  }, []);

  useEffect(() => {
    if (!stlFile || !sceneRef.current) return;

    setLoading(true);
    setError(null);

    // Remove existing mesh
    if (meshRef.current) {
      sceneRef.current.remove(meshRef.current);
      meshRef.current.geometry.dispose();
      meshRef.current.material.dispose();
    }

    const loader = new STLLoader();
    const stlUrl = `/stl/${stlFile.name}`;

    loader.load(
      stlUrl,
      (geometry) => {
        console.log('STL loaded successfully:', geometry);

        // Compute normals for proper lighting
        geometry.computeVertexNormals();

        // Create material
        const material = new THREE.MeshLambertMaterial({
          color: 0x4ecdc4,
          transparent: false,
          opacity: 1.0
        });

        // Create mesh
        const mesh = new THREE.Mesh(geometry, material);
        mesh.castShadow = true;
        mesh.receiveShadow = true;

        // Center the geometry
        geometry.computeBoundingBox();
        const center = geometry.boundingBox.getCenter(new THREE.Vector3());
        console.log('Geometry center:', center);
        geometry.translate(-center.x, -center.y, -center.z);

        // Scale to fit in view
        const size = geometry.boundingBox.getSize(new THREE.Vector3());
        console.log('Geometry size:', size);
        const maxDim = Math.max(size.x, size.y, size.z);
        const scale = 3 / maxDim; // Increased scale for better visibility
        console.log('Scale factor:', scale);
        mesh.scale.setScalar(scale);

        // Reset camera to look at the new mesh
        if (cameraRef.current) {
          cameraRef.current.position.set(5, 5, 5);
          cameraRef.current.lookAt(0, 0, 0);
        }

        sceneRef.current.add(mesh);
        meshRef.current = mesh;
        console.log('STL mesh added to scene');
        setLoading(false);
      },
      (progress) => {
        console.log('Loading progress:', (progress.loaded / progress.total) * 100 + '%');
      },
      (error) => {
        console.error('Error loading STL:', error);
        setError('Failed to load STL file');
        setLoading(false);
      }
    );
  }, [stlFile]);

  if (!isVisible) {
    console.log('Rendering placeholder - not visible');
    return (
      <div className="stl-viewer">
        <h3>3D Viewer</h3>
        <div className="viewer-placeholder">
          <p>üîß 3D STL Viewer</p>
          <p>Select an STL file to view it in 3D</p>
        </div>
      </div>
    );
  }

  console.log('Rendering STL viewer for file:', stlFile ? stlFile.name : 'no file selected');

  return (
    <div className="stl-viewer">
      <h3>3D Viewer{stlFile ? ` - ${stlFile.name}` : ''}</h3>
      <div className="viewer-container">
        {loading && (
          <div className="loading-overlay">
            <div className="loading-spinner"></div>
            <p>Loading STL file...</p>
          </div>
        )}
        {error && (
          <div className="error-overlay">
            <p>‚ùå {error}</p>
          </div>
        )}
        <div ref={mountRef} className="three-container"></div>
      </div>
      {stlFile && (
        <div className="viewer-info">
          <p><strong>File:</strong> {stlFile.name}</p>
          <p><strong>Description:</strong> {stlFile.description}</p>
          <div className="controls-info">
            <small>
              üñ±Ô∏è Left click + drag to rotate ‚Ä¢ Scroll to zoom ‚Ä¢ Right click + drag to pan
            </small>
          </div>
        </div>
      )}
    </div>
  );
}

export default STLViewer;
