import React from 'react';
import { useExperiment } from '../context/ExperimentContext';

const SwitchPanel = ({ onTaskComplete }) => {
  const { 
    designType, 
    lightStates, 
    toggleSwitch, 
    isTaskActive,
    targetAreas,
    taskSuccess
  } = useExperiment();

  const areas = ['A', 'B', 'C', 'D'];

  const handleSwitchToggle = (areaId) => {
    if (!isTaskActive) return;
    toggleSwitch(areaId);

    if (taskSuccess === true && onTaskComplete) {
      // Task completion is handled in context
    }
  };

  // Traditional switch panel layout - overlay on switch panel image
  const TraditionalLayout = () => {
    return (
      <div className="w-full">
        <h3 className="mb-6 text-gray-800 text-xl font-semibold text-center">
          Traditional Light Switch Panel
        </h3>
        <div className="flex justify-center items-center">
          <div className="relative bg-white rounded-lg shadow-lg" style={{ 
            padding: '30px 40px',
            maxWidth: '500px',
            width: '100%',
            boxShadow: '0 4px 6px rgba(0,0,0,0.1), inset 0 1px 0 rgba(255,255,255,0.8)'
          }}>
            {/* White panel with beveled edges */}
            <div className="relative">
              {/* 6 Switch openings arranged in 2 rows of 3 */}
              <div className="grid grid-cols-3 gap-6" style={{ minHeight: '200px' }}>
                {/* Top row */}
                <div className="relative" style={{ minHeight: '100px' }}>
                  <div
                    className="absolute inset-0 cursor-pointer transition-all duration-300 flex flex-col items-center justify-center"
                    onClick={() => handleSwitchToggle('A')}
                    style={{ opacity: isTaskActive ? 1 : 0.5 }}
                  >
                    {/* Switch opening background */}
                    <div className="w-full h-20 bg-gray-100 rounded border-2 border-gray-300 mb-2 flex items-center justify-center relative">
                      {/* Actual switch toggle - always white/gray, only inner circle lights up */}
                      <div className="w-10 h-14 rounded border-2 bg-white border-gray-400 relative transition-all">
                        <div className={`absolute w-7 h-7 rounded-full border-2 transition-all ${
                          lightStates.A ? 'bg-blue-500 border-blue-600 top-1 left-1.5' : 'bg-white border-gray-400 bottom-1 left-1.5'
                        }`}></div>
                      </div>
                      {targetAreas.includes('A') && (
                        <div className="absolute -top-2 -right-2 bg-yellow-400 text-yellow-900 text-xs font-bold px-1.5 py-0.5 rounded shadow">T</div>
                      )}
                    </div>
                    <div className="text-xs font-semibold text-gray-700">Area A</div>
                  </div>
                </div>

                <div className="relative" style={{ minHeight: '100px' }}>
                  <div
                    className="absolute inset-0 cursor-pointer transition-all duration-300 flex flex-col items-center justify-center"
                    onClick={() => handleSwitchToggle('B')}
                    style={{ opacity: isTaskActive ? 1 : 0.5 }}
                  >
                    <div className="w-full h-20 bg-gray-100 rounded border-2 border-gray-300 mb-2 flex items-center justify-center relative">
                      <div className={`w-10 h-14 rounded border-2 relative transition-all ${
                        lightStates.B ? 'bg-blue-500 border-blue-600' : 'bg-white border-gray-400'
                      }`}>
                        <div className={`absolute w-7 h-7 rounded-full bg-white border-2 transition-all ${
                          lightStates.B ? 'top-1 left-1.5 border-blue-600' : 'bottom-1 left-1.5 border-gray-400'
                        }`}></div>
                      </div>
                      {targetAreas.includes('B') && (
                        <div className="absolute -top-2 -right-2 bg-yellow-400 text-yellow-900 text-xs font-bold px-1.5 py-0.5 rounded shadow">T</div>
                      )}
                    </div>
                    <div className="text-xs font-semibold text-gray-700">Area B</div>
                  </div>
                </div>

                <div className="relative" style={{ minHeight: '100px' }}>
                  {/* Empty slot */}
                </div>

                {/* Bottom row */}
                <div className="relative" style={{ minHeight: '100px' }}>
                  <div
                    className="absolute inset-0 cursor-pointer transition-all duration-300 flex flex-col items-center justify-center"
                    onClick={() => handleSwitchToggle('C')}
                    style={{ opacity: isTaskActive ? 1 : 0.5 }}
                  >
                    <div className="w-full h-20 bg-gray-100 rounded border-2 border-gray-300 mb-2 flex items-center justify-center relative">
                      <div className={`w-10 h-14 rounded border-2 relative transition-all ${
                        lightStates.C ? 'bg-blue-500 border-blue-600' : 'bg-white border-gray-400'
                      }`}>
                        <div className={`absolute w-7 h-7 rounded-full bg-white border-2 transition-all ${
                          lightStates.C ? 'top-1 left-1.5 border-blue-600' : 'bottom-1 left-1.5 border-gray-400'
                        }`}></div>
                      </div>
                      {targetAreas.includes('C') && (
                        <div className="absolute -top-2 -right-2 bg-yellow-400 text-yellow-900 text-xs font-bold px-1.5 py-0.5 rounded shadow">T</div>
                      )}
                    </div>
                    <div className="text-xs font-semibold text-gray-700">Area C</div>
                  </div>
                </div>

                <div className="relative" style={{ minHeight: '100px' }}>
                  <div
                    className="absolute inset-0 cursor-pointer transition-all duration-300 flex flex-col items-center justify-center"
                    onClick={() => handleSwitchToggle('D')}
                    style={{ opacity: isTaskActive ? 1 : 0.5 }}
                  >
                    <div className="w-full h-20 bg-gray-100 rounded border-2 border-gray-300 mb-2 flex items-center justify-center relative">
                      <div className={`w-10 h-14 rounded border-2 relative transition-all ${
                        lightStates.D ? 'bg-blue-500 border-blue-600' : 'bg-white border-gray-400'
                      }`}>
                        <div className={`absolute w-7 h-7 rounded-full bg-white border-2 transition-all ${
                          lightStates.D ? 'top-1 left-1.5 border-blue-600' : 'bottom-1 left-1.5 border-gray-400'
                        }`}></div>
                      </div>
                      {targetAreas.includes('D') && (
                        <div className="absolute -top-2 -right-2 bg-yellow-400 text-yellow-900 text-xs font-bold px-1.5 py-0.5 rounded shadow">T</div>
                      )}
                    </div>
                    <div className="text-xs font-semibold text-gray-700">Area D</div>
                  </div>
                </div>

                <div className="relative" style={{ minHeight: '100px' }}>
                  {/* Empty slot */}
                </div>
              </div>

              {/* Screws - decorative elements matching the image */}
              <div className="absolute top-2 left-1/2 transform -translate-x-1/2 flex gap-20">
                <div className="w-2 h-2 rounded-full bg-gray-400 shadow-sm"></div>
                <div className="w-2 h-2 rounded-full bg-gray-400 shadow-sm"></div>
                <div className="w-2 h-2 rounded-full bg-gray-400 shadow-sm"></div>
              </div>
              <div className="absolute bottom-2 left-1/2 transform -translate-x-1/2 flex gap-20">
                <div className="w-2 h-2 rounded-full bg-gray-400 shadow-sm"></div>
                <div className="w-2 h-2 rounded-full bg-gray-400 shadow-sm"></div>
                <div className="w-2 h-2 rounded-full bg-gray-400 shadow-sm"></div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  // Floor plan layout - now matches FloorPlanDisplay
  const FloorPlanLayout = () => {
    return (
      <div className="w-full">
        <h3 className="mb-6 text-gray-800 text-xl font-semibold text-center">
          Floor Plan Switch Interface
        </h3>
        <div className="max-w-4xl mx-auto">
          <div className="relative" style={{ width: '100%', aspectRatio: '4/3' }}>
            {/* Outer border */}
            <div className="absolute inset-0 border-4 border-black bg-gray-50 rounded-lg"></div>

            {/* Room A - Top Left */}
            <div
              className="absolute cursor-pointer transition-all duration-300"
              style={{
                left: '0',
                top: '0',
                width: '50%',
                height: '50%',
                borderRight: '2px solid #9ca3af',
                borderBottom: '2px solid #9ca3af'
              }}
              onClick={() => handleSwitchToggle('A')}
            >
              <div className="w-full h-full flex items-center justify-center relative">
                <div
                  className={`w-20 h-20 rounded-full border-4 border-black flex items-center justify-center text-3xl font-bold transition-all duration-300 ${
                    lightStates.A
                      ? 'bg-cyan-200 border-cyan-400 shadow-lg shadow-cyan-400/50'
                      : 'bg-white border-black'
                  } ${isTaskActive ? 'hover:scale-110' : 'opacity-60 cursor-not-allowed'}`}
                >
                  A
                </div>
                {lightStates.A && (
                  <div className="absolute inset-0 bg-cyan-200/30 rounded-lg pointer-events-none"></div>
                )}
                {targetAreas.includes('A') && (
                  <div className="absolute top-2 right-2 bg-yellow-400 text-yellow-900 text-xs font-bold px-2 py-1 rounded shadow-md">
                    TARGET
                  </div>
                )}
              </div>
            </div>

            {/* Room B - Top Right */}
            <div
              className="absolute cursor-pointer transition-all duration-300"
              style={{
                left: '50%',
                top: '0',
                width: '50%',
                height: '50%',
                borderBottom: '2px solid #9ca3af'
              }}
              onClick={() => handleSwitchToggle('B')}
            >
              <div className="w-full h-full flex items-center justify-center relative">
                <div
                  className={`w-20 h-20 rounded-full border-4 border-black flex items-center justify-center text-3xl font-bold transition-all duration-300 ${
                    lightStates.B
                      ? 'bg-cyan-200 border-cyan-400 shadow-lg shadow-cyan-400/50'
                      : 'bg-white border-black'
                  } ${isTaskActive ? 'hover:scale-110' : 'opacity-60 cursor-not-allowed'}`}
                >
                  B
                </div>
                {lightStates.B && (
                  <div className="absolute inset-0 bg-cyan-200/30 rounded-lg pointer-events-none"></div>
                )}
                {targetAreas.includes('B') && (
                  <div className="absolute top-2 right-2 bg-yellow-400 text-yellow-900 text-xs font-bold px-2 py-1 rounded shadow-md">
                    TARGET
                  </div>
                )}
              </div>
            </div>

            {/* Room C - Bottom Left (larger) */}
            <div
              className="absolute cursor-pointer transition-all duration-300"
              style={{
                left: '0',
                top: '50%',
                width: '66.67%',
                height: '50%',
                borderRight: '2px solid #9ca3af'
              }}
              onClick={() => handleSwitchToggle('C')}
            >
              <div className="w-full h-full flex items-center justify-center relative">
                <div
                  className={`w-20 h-20 rounded-full border-4 border-black flex items-center justify-center text-3xl font-bold transition-all duration-300 ${
                    lightStates.C
                      ? 'bg-cyan-200 border-cyan-400 shadow-lg shadow-cyan-400/50'
                      : 'bg-white border-black'
                  } ${isTaskActive ? 'hover:scale-110' : 'opacity-60 cursor-not-allowed'}`}
                >
                  C
                </div>
                {lightStates.C && (
                  <div className="absolute inset-0 bg-cyan-200/30 rounded-lg pointer-events-none"></div>
                )}
                {targetAreas.includes('C') && (
                  <div className="absolute top-2 right-2 bg-yellow-400 text-yellow-900 text-xs font-bold px-2 py-1 rounded shadow-md">
                    TARGET
                  </div>
                )}
              </div>
            </div>

            {/* Room D - Bottom Right (smaller) */}
            <div
              className="absolute cursor-pointer transition-all duration-300"
              style={{
                left: '66.67%',
                top: '50%',
                width: '33.33%',
                height: '50%'
              }}
              onClick={() => handleSwitchToggle('D')}
            >
              <div className="w-full h-full flex items-center justify-center relative">
                <div
                  className={`w-20 h-20 rounded-full border-4 border-black flex items-center justify-center text-3xl font-bold transition-all duration-300 ${
                    lightStates.D
                      ? 'bg-cyan-200 border-cyan-400 shadow-lg shadow-cyan-400/50'
                      : 'bg-white border-black'
                  } ${isTaskActive ? 'hover:scale-110' : 'opacity-60 cursor-not-allowed'}`}
                >
                  D
                </div>
                {lightStates.D && (
                  <div className="absolute inset-0 bg-cyan-200/30 rounded-lg pointer-events-none"></div>
                )}
                {targetAreas.includes('D') && (
                  <div className="absolute top-2 right-2 bg-yellow-400 text-yellow-900 text-xs font-bold px-2 py-1 rounded shadow-md">
                    TARGET
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="w-full p-6 bg-white rounded-xl shadow-lg">
      {designType === 'traditional' ? <TraditionalLayout /> : <FloorPlanLayout />}

      {/* Task status indicator */}
      {isTaskActive && (
        <div className="mt-6 text-center">
          <div className="inline-block px-4 py-2 bg-green-100 text-green-800 rounded-full text-sm font-medium">
            Task Active - Turn ON Areas: {targetAreas.join(', ')}
          </div>
        </div>
      )}

      {taskSuccess === true && (
        <div className="mt-6 text-center">
          <div className="inline-block px-4 py-2 bg-green-500 text-white rounded-full text-sm font-bold animate-pulse">
            âœ“ Task Completed Successfully!
          </div>
        </div>
      )}
    </div>
  );
};

export default SwitchPanel;
