import React from 'react';
import { useExperiment } from '../context/ExperimentContext';

const FloorPlanDisplay = () => {
  const { lightStates, targetAreas, isTaskActive, toggleSwitch, roomLayout } = useExperiment();

  const handleAreaClick = (areaId) => {
    if (isTaskActive) {
      toggleSwitch(areaId);
    }
  };

  // Use room layout if available, otherwise default to 4 rooms
  const rooms = roomLayout?.rooms || [
    { id: 'A', left: 0, top: 0, width: 400, height: 300 },
    { id: 'B', left: 400, top: 0, width: 400, height: 300 },
    { id: 'C', left: 0, top: 300, width: 533, height: 300 },
    { id: 'D', left: 533, top: 300, width: 267, height: 300 }
  ];

  return (
    <div className="w-full p-6 bg-white rounded-xl shadow-lg">
      <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">
        Floor Plan View
      </h2>


    </div>
  );
};

export default FloorPlanDisplay;
