import React from 'react';
import { useExperiment } from '../context/ExperimentContext';

const SwitchPanel = ({ onTaskComplete }) => {
  const { 
    designType, 
    lightStates, 
    toggleSwitch, 
    isTaskActive,
    targetAreas,
    lightsToTurnOff,
    taskSuccess,
    roomLayout,
    currentCondition
  } = useExperiment();

  const areas = roomLayout?.roomIds || ['A', 'B', 'C', 'D'];

  const handleSwitchToggle = (areaId) => {
    if (!isTaskActive) return;
    toggleSwitch(areaId);

    if (taskSuccess === true && onTaskComplete) {
      // Task completion is handled in context
    }
  };

  // Traditional switch panel layout - overlay on switch panel image with wall background
  const TraditionalLayout = () => {
    // Position switches evenly in one row, centered
    const getSwitchPosition = (index, total) => {
      const centerOffset = 50; // Center position
      const spacing = 60 / (total + 1); // Spacing between switches
      const startOffset = centerOffset - (spacing * (total - 1) / 2);
      return {
        top: '50%',
        left: `${startOffset + spacing * index}%`,
        transform: 'translate(-50%, -50%)'
      };
    };

    return (
      <div className="w-full">
        <h3 className="mb-6 text-gray-800 text-xl font-semibold text-center">
          Traditional Light Switch Panel
        </h3>
        <div className="flex justify-center items-center">
          <div className="relative" style={{ 
            maxWidth: '800px',
            width: '100%',
            aspectRatio: '4/3',
            backgroundImage: 'url(/stl/wall.jpg)',
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            backgroundRepeat: 'no-repeat',
            borderRadius: '8px',
            overflow: 'hidden'
          }}>
            {/* Switch panel image overlay - centered */}
            <div className="absolute inset-0 flex items-center justify-center">
              <img 
                src="/stl/61tvXvpALEL._AC_UF894,1000_QL80_.jpg" 
                alt="Switch Panel"
                className="max-w-full max-h-full object-contain"
                style={{ 
                  maxWidth: '400px',
                  maxHeight: '300px',
                  opacity: 0.9
                }}
              />
            </div>

            {/* Overlay switches on the image - centered */}
            {areas.map((area, index) => {
              const pos = getSwitchPosition(index, areas.length);
              return (
                <div
                  key={area}
                  className="absolute cursor-pointer transition-all duration-300 flex flex-col items-center justify-center"
                  style={{
                    top: pos.top,
                    left: pos.left,
                    transform: pos.transform,
                    opacity: isTaskActive ? 1 : 0.5
                  }}
                  onClick={() => handleSwitchToggle(area)}
                >
                  {/* Vertical toggle switch */}
                  <div 
                    className={`w-8 h-16 bg-gray-600 rounded-2xl relative transition-all duration-300 border-2 border-gray-700 ${
                      isTaskActive 
                        ? '' 
                        : 'opacity-50 cursor-not-allowed'
                    }`}
                    style={{ overflow: 'hidden' }}
                  >
                    <div 
                      className="w-6 h-6 bg-white rounded-full absolute transition-all duration-300 shadow-md"
                      style={{
                        top: lightStates[area] ? '4px' : 'calc(100% - 28px)',
                        left: '50%',
                        transform: 'translateX(-50%)'
                      }}
                    ></div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    );
  };

  // Design B: Buttons below floor plan with wall background
  const ButtonsBelowLayout = () => {
    const rooms = roomLayout?.rooms || [
      { id: 'A', left: 0, top: 0, width: 400, height: 300 },
      { id: 'B', left: 400, top: 0, width: 400, height: 300 },
      { id: 'C', left: 0, top: 300, width: 533, height: 300 },
      { id: 'D', left: 533, top: 300, width: 267, height: 300 }
    ];

    // Calculate the bounds of the floor plan
    const maxWidth = Math.max(...rooms.map(r => r.left + r.width));
    const maxHeight = Math.max(...rooms.map(r => r.top + r.height));
    const floorPlanWidth = maxWidth;
    const floorPlanHeight = maxHeight;

    return (
      <div className="w-full">
        <h3 className="mb-6 text-gray-800 text-xl font-semibold text-center">
          Buttons Below Floor Plan Interface
        </h3>

        {/* Container with wall background */}
        <div className="flex justify-center items-center" style={{
          backgroundImage: 'url(/stl/wall.jpg)',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat',
          borderRadius: '8px',
          padding: '40px 20px',
          minHeight: '600px'
        }}>
          <div className="flex flex-col items-center justify-center gap-6">
            {/* Floor plan visualization (non-interactive) - centered */}
            <div 
              className="relative border-2 border-gray-400 bg-gray-50/90 rounded-lg shadow-lg"
              style={{
                width: '500px',
                height: '375px',
                maxWidth: '90%'
              }}
            >
              {/* Render rooms from layout - scaled to fit */}
              {rooms.map((room, index) => {
                // Check if there's a room to the right or below
                const hasRoomRight = rooms.some(r => 
                  Math.abs(r.top - room.top) < 5 && 
                  Math.abs(r.left - (room.left + r.width)) < 5
                );
                const hasRoomBelow = rooms.some(r => 
                  Math.abs(r.left - room.left) < 5 && 
                  Math.abs(r.top - (room.top + r.height)) < 5
                );

                // Scale positions to fit in container
                const scaleX = 500 / floorPlanWidth;
                const scaleY = 375 / floorPlanHeight;

                return (
                  <div
                    key={room.id}
                    className="absolute transition-all duration-300"
                    style={{
                      left: `${room.left * scaleX}px`,
                      top: `${room.top * scaleY}px`,
                      width: `${room.width * scaleX}px`,
                      height: `${room.height * scaleY}px`,
                      borderRight: hasRoomRight ? '2px solid rgba(156, 163, 175, 0.6)' : 'none',
                      borderBottom: hasRoomBelow ? '2px solid rgba(156, 163, 175, 0.6)' : 'none',
                      backgroundColor: lightStates[room.id] ? 'rgba(34, 211, 238, 0.2)' : 'transparent'
                    }}
                  >
                    <div className="w-full h-full flex items-center justify-center">
                      <span className="text-2xl font-bold text-gray-700">{room.id}</span>
                    </div>
                  </div>
                );
              })}
            </div>

            {/* Buttons below floor plan - centered */}
            <div className="flex justify-center items-center gap-4 flex-wrap">
              {areas.map((area) => (
                <button
                  key={area}
                  onClick={() => handleSwitchToggle(area)}
                  disabled={!isTaskActive}
                  className={`w-20 h-20 rounded-full border-4 border-black transition-all duration-300 flex items-center justify-center text-2xl font-bold shadow-lg ${
                    lightStates[area]
                      ? 'bg-cyan-200 border-cyan-400 shadow-cyan-400/50'
                      : 'bg-white border-black'
                  } ${
                    isTaskActive 
                      ? 'cursor-pointer hover:shadow-xl' 
                      : 'opacity-60 cursor-not-allowed'
                  }`}
                >
                  {area}
                </button>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  };

  // Floor plan layout - overlay on switch panel image with wall background (Design C)
  const FloorPlanLayout = () => {
    const rooms = roomLayout?.rooms || [
      { id: 'A', left: 0, top: 0, width: 400, height: 300 },
      { id: 'B', left: 400, top: 0, width: 400, height: 300 },
      { id: 'C', left: 0, top: 300, width: 533, height: 300 },
      { id: 'D', left: 533, top: 300, width: 267, height: 300 }
    ];

    // Calculate the bounds of the floor plan
    const maxWidth = Math.max(...rooms.map(r => r.left + r.width));
    const maxHeight = Math.max(...rooms.map(r => r.top + r.height));
    const floorPlanWidth = maxWidth;
    const floorPlanHeight = maxHeight;

    // Scale factor to fit floor plan within container
    const scale = 0.6;

    return (
      <div className="w-full">
        <h3 className="mb-6 text-gray-800 text-xl font-semibold text-center">
          Floor Plan Switch Interface
        </h3>
        <div className="flex justify-center items-center">
          <div className="relative" style={{ 
            maxWidth: '800px',
            width: '100%',
            aspectRatio: '4/3',
            backgroundImage: 'url(/stl/wall.jpg)',
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            backgroundRepeat: 'no-repeat',
            borderRadius: '8px',
            overflow: 'hidden'
          }}>
            {/* Switch panel image overlay - centered */}
            <div className="absolute inset-0 flex items-center justify-center">
              <img 
                src="/stl/61tvXvpALEL._AC_UF894,1000_QL80_.jpg" 
                alt="Switch Panel"
                className="max-w-full max-h-full object-contain"
                style={{ 
                  maxWidth: '400px',
                  maxHeight: '300px',
                  opacity: 0.9
                }}
              />
            </div>

            {/* Overlay floor plan on the image - centered and scaled */}
            <div 
              className="absolute"
              style={{
                top: '50%',
                left: '50%',
                transform: `translate(-50%, -50%) scale(${scale})`,
                transformOrigin: 'center center',
                width: `${floorPlanWidth}px`,
                height: `${floorPlanHeight}px`,
                pointerEvents: 'auto'
              }}
            >
              {/* Floor plan container with border */}
              <div 
                className="relative border-2 border-gray-400 bg-gray-50/90 rounded-lg shadow-lg"
                style={{
                  width: '100%',
                  height: '100%'
                }}
              >
                {/* Render rooms from layout */}
                {rooms.map((room, index) => {
                  // Check if there's a room to the right or below
                  const hasRoomRight = rooms.some(r => 
                    Math.abs(r.top - room.top) < 5 && 
                    Math.abs(r.left - (room.left + room.width)) < 5
                  );
                  const hasRoomBelow = rooms.some(r => 
                    Math.abs(r.left - room.left) < 5 && 
                    Math.abs(r.top - (room.top + room.height)) < 5
                  );

                  return (
                    <div
                      key={room.id}
                      className="absolute cursor-pointer transition-all duration-300"
                      style={{
                        left: `${room.left}px`,
                        top: `${room.top}px`,
                        width: `${room.width}px`,
                        height: `${room.height}px`,
                        borderRight: hasRoomRight ? '2px solid rgba(156, 163, 175, 0.6)' : 'none',
                        borderBottom: hasRoomBelow ? '2px solid rgba(156, 163, 175, 0.6)' : 'none'
                      }}
                      onClick={() => handleSwitchToggle(room.id)}
                    >
                      <div className="w-full h-full flex items-center justify-center relative">
                        {/* Circular button without letter */}
                        <div
                          className={`w-12 h-12 rounded-full border-2 border-black transition-all duration-300 ${
                            lightStates[room.id]
                              ? 'bg-cyan-200 border-cyan-400 shadow-lg shadow-cyan-400/50'
                              : 'bg-white border-black'
                          } ${isTaskActive ? '' : 'opacity-60 cursor-not-allowed'}`}
                        >
                        </div>
                        {/* Indicator light when ON */}
                        {lightStates[room.id] && (
                          <div className="absolute inset-0 bg-cyan-200/30 rounded-lg pointer-events-none"></div>
                        )}
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="w-full p-6 bg-white rounded-xl shadow-lg">
      {designType === 'traditional' ? <TraditionalLayout /> : 
       designType === 'buttons' ? <ButtonsBelowLayout /> : 
       <FloorPlanLayout />}

      {/* Task status indicator */}
      {isTaskActive && (
        <div className="mt-6 text-center">
          <div className="inline-block px-4 py-2 bg-green-100 text-green-800 rounded-full text-sm font-medium">
            {(() => {
              const taskType = currentCondition?.taskType || 'turnOn2';
              if (taskType === 'turnOn2') {
                return `Task Active - Turn ON 2 lights: ${targetAreas.join(', ')}`;
              } else if (taskType === 'turnOn1Off1') {
                return `Task Active - Turn ON: ${targetAreas.join(', ')}, Turn OFF: ${lightsToTurnOff.join(', ')}`;
              }
              return `Task Active - Turn ON Areas: ${targetAreas.join(', ')}`;
            })()}
          </div>
        </div>
      )}

      {taskSuccess === true && (
        <div className="mt-6 text-center">
          <div className="inline-block px-4 py-2 bg-green-500 text-white rounded-full text-sm font-bold animate-pulse">
            âœ“ Task Completed Successfully!
          </div>
        </div>
      )}
    </div>
  );
};

export default SwitchPanel;
