import React, { useState, useEffect } from 'react';
import RoomLayout from './components/RoomLayout';
import './App.css';

function App() {
  const [rooms, setRooms] = useState([]);
  const [switchStates, setSwitchStates] = useState({});
  const [activeTab, setActiveTab] = useState('floorplan');

  // Generate random number of rooms (4-8) and create room layout
  useEffect(() => {
    generateNewLayout();
  }, []);

  const generateNewLayout = () => {
    const numRooms = Math.floor(Math.random() * 5) + 4; // 4-8 rooms
    const newRooms = [];
    const newSwitchStates = {};

    // Container dimensions
    const containerWidth = 800;
    const containerHeight = 600;

    // Generate creative floor plan with potential negative spaces
    const { rooms, lightAreas } = generateCreativeFloorPlan(numRooms, containerWidth, containerHeight);

    rooms.forEach((room, i) => {
      const roomId = String.fromCharCode(65 + i); // A, B, C, D, etc.
      newRooms.push({
        id: roomId,
        label: roomId,
        isActive: false,
        width: room.width,
        height: room.height,
        left: room.left,
        top: room.top,
        type: 'room'
      });
      newSwitchStates[roomId] = false;
    });

    // Add light areas for negative spaces
    lightAreas.forEach((area, i) => {
      const lightId = `L${i + 1}`;
      newRooms.push({
        id: lightId,
        label: lightId,
        isActive: false,
        width: area.width,
        height: area.height,
        left: area.left,
        top: area.top,
        type: 'light'
      });
      newSwitchStates[lightId] = false;
    });

    setRooms(newRooms);
    setSwitchStates(newSwitchStates);
  };

  // Function to generate creative floor plan with negative spaces and light areas
  const generateCreativeFloorPlan = (numRooms, containerWidth, containerHeight) => {
    const rooms = [];
    const lightAreas = [];
    const margin = 15;

    // Create more organic, realistic floor plans with proper spacing
    const layouts = [
      // L-shaped layout
      () => {
        const room1 = { width: 250, height: 180, left: margin, top: margin };
        const room2 = { width: 200, height: 180, left: room1.width + margin * 2, top: margin };
        const room3 = { width: 250, height: 150, left: margin, top: room1.height + margin * 2 };
        const room4 = { width: 200, height: 150, left: room3.width + margin * 2, top: room2.height + margin * 2 };

        // Add light area in the corner - ensure no overlap
        const lightArea = { 
          width: 120, 
          height: 80, 
          left: room2.width + room1.width + margin * 3, 
          top: room2.height + margin * 2 
        };

        return { rooms: [room1, room2, room3, room4], lightAreas: [lightArea] };
      },

      // Central corridor layout
      () => {
        const room1 = { width: 200, height: 150, left: margin, top: margin };
        const room2 = { width: 200, height: 150, left: room1.width + margin * 2, top: margin };
        const room3 = { width: 200, height: 150, left: room2.width + room1.width + margin * 3, top: margin };
        const room4 = { width: 200, height: 150, left: margin, top: room1.height + margin * 2 };
        const room5 = { width: 200, height: 150, left: room4.width + margin * 2, top: room2.height + margin * 2 };
        const room6 = { width: 200, height: 150, left: room5.width + room4.width + margin * 3, top: room3.height + margin * 2 };

        // Central corridor light area - positioned between rooms
        const lightArea = { 
          width: 200, 
          height: 150, 
          left: room4.width + margin * 2, 
          top: room1.height + margin * 2 
        };

        return { rooms: [room1, room2, room3, room4, room5, room6], lightAreas: [lightArea] };
      },

      // Scattered layout with multiple light areas
      () => {
        const room1 = { width: 180, height: 120, left: margin, top: margin };
        const room2 = { width: 160, height: 150, left: room1.width + margin * 2, top: margin };
        const room3 = { width: 200, height: 100, left: margin, top: room1.height + margin * 2 };
        const room4 = { width: 140, height: 130, left: room3.width + margin * 2, top: room2.height + margin * 2 };
        const room5 = { width: 170, height: 110, left: room4.width + room3.width + margin * 3, top: room2.height + margin * 2 };

        // Multiple light areas - positioned to avoid overlaps
        const lightArea1 = { width: 80, height: 60, left: room2.width + room1.width + margin * 2, top: room1.height + margin * 2 };
        const lightArea2 = { width: 100, height: 50, left: room5.width + room4.width + room3.width + margin * 4, top: margin };

        return { rooms: [room1, room2, room3, room4, room5], lightAreas: [lightArea1, lightArea2] };
      },

      // U-shaped layout
      () => {
        const room1 = { width: 180, height: 200, left: margin, top: margin };
        const room2 = { width: 250, height: 100, left: room1.width + margin * 2, top: margin };
        const room3 = { width: 180, height: 200, left: room2.width + room1.width + margin * 3, top: margin };
        const room4 = { width: 180, height: 150, left: margin, top: room1.height + margin * 2 };
        const room5 = { width: 180, height: 150, left: room4.width + room2.width + margin * 3, top: room2.height + margin * 2 };

        // Central courtyard light area - positioned in the center
        const lightArea = { 
          width: 250, 
          height: 150, 
          left: room1.width + margin * 2, 
          top: room2.height + margin * 2 
        };

        return { rooms: [room1, room2, room3, room4, room5], lightAreas: [lightArea] };
      }
    ];

    // Select a random layout
    const selectedLayout = layouts[Math.floor(Math.random() * layouts.length)];
    return selectedLayout();
  };

  const toggleSwitch = (roomId) => {
    setSwitchStates(prev => ({
      ...prev,
      [roomId]: !prev[roomId]
    }));
  };

  return (
    <div className="app">
      <div className="app-header">
        <h1>Virtual Room Mock-up</h1>
        <div className="header-controls">
          <div className="tab-buttons">
            <button 
              className={`tab-btn ${activeTab === 'floorplan' ? 'active' : ''}`}
              onClick={() => setActiveTab('floorplan')}
            >
              Floor Plan
            </button>
            <button 
              className={`tab-btn ${activeTab === 'switches' ? 'active' : ''}`}
              onClick={() => setActiveTab('switches')}
            >
              Traditional Switches
            </button>
          </div>
          <button onClick={generateNewLayout} className="regenerate-btn">
            Generate New Layout
          </button>
        </div>
      </div>

      <div className="app-content">
        {activeTab === 'floorplan' && (
          <div className="layout-section">
            <h2>Floor Plan Layout</h2>
            <div className="floorplan-container">
              <RoomLayout 
                rooms={rooms} 
                switchStates={switchStates}
                onToggleSwitch={toggleSwitch}
              />

              <div className="floorplan-status">
                <h3>Lighting Status</h3>
                <div className="status-overview">
                  <div className="status-card">
                    <div className="status-icon">üè†</div>
                    <div className="status-info">
                      <div className="status-number">
                        {rooms.filter(room => room.type === 'room' && switchStates[room.id]).length}
                      </div>
                      <div className="status-text">Rooms On</div>
                    </div>
                  </div>

                  <div className="status-card">
                    <div className="status-icon">üí°</div>
                    <div className="status-info">
                      <div className="status-number">
                        {rooms.filter(room => room.type === 'light' && switchStates[room.id]).length}
                      </div>
                      <div className="status-text">Light Areas On</div>
                    </div>
                  </div>

                  <div className="status-card">
                    <div className="status-icon">‚ö°</div>
                    <div className="status-info">
                      <div className="status-number">
                        {rooms.filter(room => switchStates[room.id]).length}
                      </div>
                      <div className="status-text">Total Active</div>
                    </div>
                  </div>
                </div>

                <div className="active-rooms">
                  <h4>Currently Active:</h4>
                  <div className="active-tags">
                    {rooms.filter(room => switchStates[room.id]).map(room => (
                      <span key={room.id} className={`active-tag ${room.type === 'light' ? 'light-tag' : 'room-tag'}`}>
                        {room.label} {room.type === 'light' ? '(Light)' : '(Room)'}
                      </span>
                    ))}
                    {rooms.filter(room => switchStates[room.id]).length === 0 && (
                      <span className="no-active-tag">No lights active</span>
                    )}
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {activeTab === 'switches' && (
          <div className="switch-panel-section">
            <h2>Traditional Light Switch Panel</h2>
            <div className="switch-panel">
              {rooms.map((room) => (
                <div key={room.id} className="switch-item">
                  <div className="switch-label">{room.label}</div>
                  <div className="switch-description">
                    {room.type === 'light' ? 'Light Area' : 'Room'}
                  </div>
                  <div 
                    className={`traditional-switch ${switchStates[room.id] ? 'on' : 'off'}`}
                    onClick={() => toggleSwitch(room.id)}
                  >
                    <div className="switch-toggle"></div>
                  </div>
                  <div className={`status-indicator ${switchStates[room.id] ? 'active' : 'inactive'}`}>
                    {switchStates[room.id] ? 'ON' : 'OFF'}
                  </div>
                </div>
              ))}
            </div>

            <div className="status-summary">
              <h3>Current Lighting Status</h3>
              <div className="status-grid">
                <div className="status-item">
                  <div className="status-label">Active Rooms</div>
                  <div className="status-count">
                    {rooms.filter(room => room.type === 'room' && switchStates[room.id]).length}
                  </div>
                </div>
                <div className="status-item">
                  <div className="status-label">Active Light Areas</div>
                  <div className="status-count">
                    {rooms.filter(room => room.type === 'light' && switchStates[room.id]).length}
                  </div>
                </div>
                <div className="status-item">
                  <div className="status-label">Total Active</div>
                  <div className="status-count">
                    {rooms.filter(room => switchStates[room.id]).length}
                  </div>
                </div>
              </div>

              <div className="active-list">
                <h4>Currently Active:</h4>
                <div className="active-items">
                  {rooms.filter(room => switchStates[room.id]).map(room => (
                    <div key={room.id} className="active-item">
                      <span className="active-label">{room.label}</span>
                      <span className="active-type">({room.type === 'light' ? 'Light Area' : 'Room'})</span>
                    </div>
                  ))}
                  {rooms.filter(room => switchStates[room.id]).length === 0 && (
                    <div className="no-active">No lights currently active</div>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
