import React from 'react';
import { useExperiment } from '../context/ExperimentContext';

const SwitchPanel = ({ onTaskComplete }) => {
  const { 
    designType, 
    lightStates, 
    toggleSwitch, 
    isTaskActive,
    targetAreas,
    taskSuccess
  } = useExperiment();

  const areas = ['A', 'B', 'C', 'D'];

  const handleSwitchToggle = (areaId) => {
    if (!isTaskActive) return;
    toggleSwitch(areaId);

    if (taskSuccess === true && onTaskComplete) {
      // Task completion is handled in context
    }
  };

  // Traditional switch panel layout - overlay on switch panel image
  const TraditionalLayout = () => {
    // Create SVG for the switch panel background
    const SwitchPanelSVG = () => (
      <svg
        viewBox="0 0 400 300"
        className="w-full h-auto"
        style={{ maxWidth: '500px' }}
      >
        {/* White panel background */}
        <rect
          x="20"
          y="20"
          width="360"
          height="260"
          rx="8"
          fill="#ffffff"
          stroke="#e5e7eb"
          strokeWidth="2"
        />

        {/* 6 Switch openings arranged in 2 rows of 3 */}
        {/* Top row */}
        <rect x="60" y="60" width="80" height="100" rx="4" fill="#f3f4f6" stroke="#d1d5db" strokeWidth="1" />
        <rect x="160" y="60" width="80" height="100" rx="4" fill="#f3f4f6" stroke="#d1d5db" strokeWidth="1" />
        <rect x="260" y="60" width="80" height="100" rx="4" fill="#f3f4f6" stroke="#d1d5db" strokeWidth="1" />

        {/* Bottom row */}
        <rect x="60" y="180" width="80" height="100" rx="4" fill="#f3f4f6" stroke="#d1d5db" strokeWidth="1" />
        <rect x="160" y="180" width="80" height="100" rx="4" fill="#f3f4f6" stroke="#d1d5db" strokeWidth="1" />
        <rect x="260" y="180" width="80" height="100" rx="4" fill="#f3f4f6" stroke="#d1d5db" strokeWidth="1" />

        {/* Screws - top row */}
        <circle cx="100" cy="50" r="3" fill="#9ca3af" />
        <circle cx="200" cy="50" r="3" fill="#9ca3af" />
        <circle cx="300" cy="50" r="3" fill="#9ca3af" />

        {/* Screws - bottom row */}
        <circle cx="100" cy="250" r="3" fill="#9ca3af" />
        <circle cx="200" cy="250" r="3" fill="#9ca3af" />
        <circle cx="300" cy="250" r="3" fill="#9ca3af" />
      </svg>
    );

    // Map areas to switch positions (using 4 of the 6 slots in a 2x2 grid)
    const switchPositions = {
      A: { row: 0, col: 0, x: 60, y: 60 },   // Top left
      B: { row: 0, col: 1, x: 160, y: 60 },  // Top middle
      C: { row: 1, col: 0, x: 60, y: 180 },  // Bottom left
      D: { row: 1, col: 1, x: 160, y: 180 }  // Bottom middle
    };

    return (
      <div className="w-full">
        <h3 className="mb-6 text-gray-800 text-xl font-semibold text-center">
          Traditional Light Switch Panel
        </h3>
        <div className="flex justify-center items-center">
          <div className="relative" style={{ maxWidth: '500px', width: '100%' }}>
            {/* Switch panel background */}
            <SwitchPanelSVG />

            {/* Overlay switches for each area */}
            {areas.map((area) => {
              const pos = switchPositions[area];
              const isOn = lightStates[area];
              const isTarget = targetAreas.includes(area);

              return (
                <div
                  key={area}
                  className="absolute"
                  style={{
                    left: `${(pos.x / 400) * 100}%`,
                    top: `${(pos.y / 300) * 100}%`,
                    width: `${(80 / 400) * 100}%`,
                    height: `${(100 / 300) * 100}%`,
