import React from 'react';
import { useExperiment } from '../context/ExperimentContext';

const FloorPlanDisplay = () => {
  const { lightStates, targetAreas, isTaskActive, toggleSwitch, roomLayout } = useExperiment();

  const handleAreaClick = (areaId) => {
    if (isTaskActive) {
      toggleSwitch(areaId);
    }
  };

  // Use room layout if available, otherwise default to 4 rooms
  const rooms = roomLayout?.rooms || [
    { id: 'A', left: 0, top: 0, width: 400, height: 300 },
    { id: 'B', left: 400, top: 0, width: 400, height: 300 },
    { id: 'C', left: 0, top: 300, width: 533, height: 300 },
    { id: 'D', left: 533, top: 300, width: 267, height: 300 }
  ];

  return (
    <div className="w-full p-6 bg-white rounded-xl shadow-lg">
      <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">
        Floor Plan View
      </h2>

      {/* Floor Plan Container - dynamic layout */}
      <div className="max-w-4xl mx-auto">
        <div className="relative border-4 border-black bg-gray-50 rounded-lg" style={{ 
          width: '100%', 
          aspectRatio: '4/3',
          maxWidth: '800px',
          maxHeight: '600px'
        }}>
          {/* Render rooms from layout */}
          {rooms.map((room, index) => {
            const isLastInRow = index < rooms.length - 1 && 
              Math.abs(room.top + room.height - rooms[index + 1].top) < 10;
            const isLastInCol = index < rooms.length - 1 && 
              Math.abs(room.left + room.width - rooms[index + 1].left) < 10;

            return (
              <div
                key={room.id}
                className="absolute cursor-pointer transition-all duration-300"
                style={{
                  left: `${(room.left / 800) * 100}%`,
                  top: `${(room.top / 600) * 100}%`,
                  width: `${(room.width / 800) * 100}%`,
                  height: `${(room.height / 600) * 100}%`,
                  borderRight: isLastInRow ? '2px solid #9ca3af' : 'none',
                  borderBottom: isLastInCol ? '2px solid #9ca3af' : 'none'
                }}
                onClick={() => handleAreaClick(room.id)}
              >
                <div className="w-full h-full flex items-center justify-center relative">
                  {/* Circular button with letter */}
                  <div
                    className={`w-16 h-16 rounded-full border-4 border-black flex items-center justify-center text-2xl font-bold transition-all duration-300 ${
                      lightStates[room.id]
                        ? 'bg-cyan-200 border-cyan-400 shadow-lg shadow-cyan-400/50'
                        : 'bg-white border-black'
                    } ${
                      isTaskActive ? '' : 'opacity-60 cursor-not-allowed'
                    }`}
                  >
                    {room.id}
                  </div>
                  {/* Indicator light when ON */}
                  {lightStates[room.id] && (
                    <div className="absolute inset-0 bg-cyan-200/30 rounded-lg pointer-events-none"></div>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Legend */}
      <div className="mt-6 flex justify-center gap-6 text-sm">
        <div className="flex items-center gap-2">
          <div className="w-5 h-5 rounded-full border-4 border-black bg-cyan-200"></div>
          <span className="text-gray-700">Light ON</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-5 h-5 rounded-full border-4 border-black bg-white"></div>
          <span className="text-gray-700">Light OFF</span>
        </div>
      </div>
    </div>
  );
};

export default FloorPlanDisplay;
