import React from 'react';
import { useExperiment } from '../context/ExperimentContext';

const SwitchPanel = ({ onTaskComplete }) => {
  const { 
    designType, 
    lightStates, 
    toggleSwitch, 
    isTaskActive,
    targetAreas,
    taskSuccess
  } = useExperiment();

  const areas = ['A', 'B', 'C', 'D'];

  const handleSwitchToggle = (areaId) => {
    if (!isTaskActive) return;
    toggleSwitch(areaId);

    if (taskSuccess === true && onTaskComplete) {
      // Task completion is handled in context
    }
  };

  // Traditional switch panel layout - overlay on switch panel image
  const TraditionalLayout = () => {
    // Position mappings for switches on the image (2x2 grid in top-left 4 positions)
    // These are approximate positions - adjust based on actual image layout
    const switchPositions = {
      A: { top: '15%', left: '12%' },   // Top left
      B: { top: '15%', left: '42%' },   // Top middle
      C: { top: '50%', left: '12%' },   // Bottom left
      D: { top: '50%', left: '42%' }    // Bottom middle
    };

    return (
      <div className="w-full">
        <h3 className="mb-6 text-gray-800 text-xl font-semibold text-center">
          Traditional Light Switch Panel
        </h3>
        <div className="flex justify-center items-center">
          <div className="relative" style={{ 
            maxWidth: '500px',
            width: '100%'
          }}>
            {/* Switch panel image background */}
            <img 
              src="/stl/61tvXvpALEL._AC_UF894,1000_QL80_.jpg" 
              alt="Switch Panel"
              className="w-full h-auto rounded-lg"
              style={{ display: 'block' }}
            />

            {/* Overlay switches on the image */}
            {areas.map((area) => {
              const pos = switchPositions[area];
              return (
                <div
                  key={area}
                  className="absolute cursor-pointer transition-all duration-300 flex flex-col items-center justify-center"
                  style={{
                    top: pos.top,
                    left: pos.left,
                    width: '20%',
                    opacity: isTaskActive ? 1 : 0.5
                  }}
                  onClick={() => handleSwitchToggle(area)}
                >
                  {/* Vertical toggle switch */}
                  <div 
                    className={`w-8 h-16 bg-gray-50 rounded-2xl relative transition-all duration-300 border-2 border-gray-200 ${
                      isTaskActive 
                        ? '' 
                        : 'opacity-50 cursor-not-allowed'
                    }`}
                    style={{ overflow: 'hidden' }}
                  >
                    <div className={`w-6 h-6 bg-white rounded-full absolute transition-all duration-300 shadow-md ${
                      lightStates[area] 
                        ? 'top-1 left-1' 
                        : 'bottom-1 left-1'
                    }`}></div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    );
  };

  // Floor plan layout - now matches FloorPlanDisplay
  const FloorPlanLayout = () => {
    return (
      <div className="w-full">
        <h3 className="mb-6 text-gray-800 text-xl font-semibold text-center">
          Floor Plan Switch Interface
        </h3>
        <div className="max-w-4xl mx-auto">
          <div className="relative" style={{ width: '100%', aspectRatio: '4/3' }}>
            {/* Outer border */}
            <div className="absolute inset-0 border-4 border-black bg-gray-50 rounded-lg"></div>

            {/* Room A - Top Left */}
            <div
              className="absolute cursor-pointer transition-all duration-300"
              style={{
                left: '0',
                top: '0',
                width: '50%',
                height: '50%',
                borderRight: '2px solid #9ca3af',
                borderBottom: '2px solid #9ca3af'
              }}
              onClick={() => handleSwitchToggle('A')}
            >
              <div className="w-full h-full flex items-center justify-center relative">
                <div
                  className={`w-20 h-20 rounded-full border-4 border-black flex items-center justify-center text-3xl font-bold transition-all duration-300 ${
                    lightStates.A
                      ? 'bg-cyan-200 border-cyan-400 shadow-lg shadow-cyan-400/50'
                      : 'bg-white border-black'
                  } ${isTaskActive ? 'hover:scale-110' : 'opacity-60 cursor-not-allowed'}`}
                >
                  A
                </div>
                {lightStates.A && (
                  <div className="absolute inset-0 bg-cyan-200/30 rounded-lg pointer-events-none"></div>
                )}
                {targetAreas.includes('A') && (
                  <div className="absolute top-2 right-2 bg-yellow-400 text-yellow-900 text-xs font-bold px-2 py-1 rounded shadow-md">
                    TARGET
                  </div>
                )}
              </div>
            </div>

            {/* Room B - Top Right */}
            <div
              className="absolute cursor-pointer transition-all duration-300"
              style={{
                left: '50%',
                top: '0',
                width: '50%',
                height: '50%',
                borderBottom: '2px solid #9ca3af'
              }}
              onClick={() => handleSwitchToggle('B')}
            >
              <div className="w-full h-full flex items-center justify-center relative">
                <div
                  className={`w-20 h-20 rounded-full border-4 border-black flex items-center justify-center text-3xl font-bold transition-all duration-300 ${
                    lightStates.B
                      ? 'bg-cyan-200 border-cyan-400 shadow-lg shadow-cyan-400/50'
                      : 'bg-white border-black'
                  } ${isTaskActive ? 'hover:scale-110' : 'opacity-60 cursor-not-allowed'}`}
                >
                  B
                </div>
                {lightStates.B && (
                  <div className="absolute inset-0 bg-cyan-200/30 rounded-lg pointer-events-none"></div>
                )}
              </div>
            </div>

            {/* Room C - Bottom Left (larger) */}
            <div
              className="absolute cursor-pointer transition-all duration-300"
              style={{
                left: '0',
                top: '50%',
                width: '66.67%',
                height: '50%',
                borderRight: '2px solid #9ca3af'
              }}
              onClick={() => handleSwitchToggle('C')}
            >
              <div className="w-full h-full flex items-center justify-center relative">
                <div
                  className={`w-20 h-20 rounded-full border-4 border-black flex items-center justify-center text-3xl font-bold transition-all duration-300 ${
                    lightStates.C
                      ? 'bg-cyan-200 border-cyan-400 shadow-lg shadow-cyan-400/50'
                      : 'bg-white border-black'
                  } ${isTaskActive ? 'hover:scale-110' : 'opacity-60 cursor-not-allowed'}`}
                >
                  C
                </div>
                {lightStates.C && (
                  <div className="absolute inset-0 bg-cyan-200/30 rounded-lg pointer-events-none"></div>
                )}
              </div>
            </div>

            {/* Room D - Bottom Right (smaller) */}
            <div
              className="absolute cursor-pointer transition-all duration-300"
              style={{
                left: '66.67%',
                top: '50%',
                width: '33.33%',
                height: '50%'
              }}
              onClick={() => handleSwitchToggle('D')}
            >
              <div className="w-full h-full flex items-center justify-center relative">
                <div
                  className={`w-20 h-20 rounded-full border-4 border-black flex items-center justify-center text-3xl font-bold transition-all duration-300 ${
                    lightStates.D
                      ? 'bg-cyan-200 border-cyan-400 shadow-lg shadow-cyan-400/50'
                      : 'bg-white border-black'
                  } ${isTaskActive ? 'hover:scale-110' : 'opacity-60 cursor-not-allowed'}`}
                >
                  D
                </div>
                {lightStates.D && (
                  <div className="absolute inset-0 bg-cyan-200/30 rounded-lg pointer-events-none"></div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="w-full p-6 bg-white rounded-xl shadow-lg">
      {designType === 'traditional' ? <TraditionalLayout /> : <FloorPlanLayout />}

      {/* Task status indicator */}
      {isTaskActive && (
        <div className="mt-6 text-center">
          <div className="inline-block px-4 py-2 bg-green-100 text-green-800 rounded-full text-sm font-medium">
            Task Active - Turn ON Areas: {targetAreas.join(', ')}
          </div>
        </div>
      )}

      {taskSuccess === true && (
        <div className="mt-6 text-center">
          <div className="inline-block px-4 py-2 bg-green-500 text-white rounded-full text-sm font-bold animate-pulse">
            âœ“ Task Completed Successfully!
          </div>
        </div>
      )}
    </div>
  );
};

export default SwitchPanel;
