import React from 'react';
import { useExperiment } from '../context/ExperimentContext';

const FloorPlanDisplay = () => {
  const { lightStates, targetAreas, isTaskActive } = useExperiment();

  const areas = ['A', 'B', 'C', 'D'];

  // Floor plan layout - grid of 4 areas
  const floorPlanStyle = {
    display: 'grid',
    gridTemplateColumns: 'repeat(2, 1fr)',
    gap: '30px',
    padding: '40px',
    maxWidth: '700px',
    margin: '0 auto',
    minHeight: '500px'
  };

  const areaStyle = (area, isOn, isTarget) => {
    const baseStyle = {
      aspectRatio: '1',
      borderRadius: '16px',
      border: `4px solid ${isOn ? '#10b981' : '#6b7280'}`,
      backgroundColor: isOn ? '#d1fae5' : '#f3f4f6',
      transition: 'all 0.4s ease',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      position: 'relative',
      boxShadow: isOn 
        ? '0 0 30px rgba(16, 185, 129, 0.6), inset 0 0 20px rgba(16, 185, 129, 0.2)' 
        : '0 4px 6px rgba(0, 0, 0, 0.1)'
    };

    return baseStyle;
  };

  return (
    <div className="w-full p-6 bg-white rounded-xl shadow-lg">
      <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">
        Floor Plan View
      </h2>

      <div style={floorPlanStyle} className="bg-gray-50 rounded-xl p-6 border-2 border-gray-200">
        {areas.map((area) => {
          const isOn = lightStates[area];
          const isTarget = targetAreas.includes(area);

          return (
            <div
              key={area}
              style={areaStyle(area, isOn, isTarget)}
            >
              {/* Light indicator */}
              <div className={`text-6xl mb-4 transition-all duration-300 ${
                isOn ? 'filter drop-shadow-lg' : 'opacity-40'
              }`}>
                {isOn ? 'ðŸ’¡' : 'âš«'}
              </div>

              {/* Area label */}
              <div className={`text-2xl font-bold mb-2 ${
                isOn ? 'text-green-700' : 'text-gray-600'
              }`}>
                Area {area}
              </div>

              {/* Status text */}
              <div className={`text-sm font-medium ${
                isOn ? 'text-green-600' : 'text-gray-500'
              }`}>
                {isOn ? 'ON' : 'OFF'}
              </div>

              {/* Target indicator */}
              {isTarget && (
                <div className="absolute top-3 right-3 bg-yellow-400 text-yellow-900 text-xs font-bold px-3 py-1 rounded-full shadow-md">
                  TARGET
                </div>
              )}

              {/* Glow effect when on */}
              {isOn && (
                <div className="absolute inset-0 rounded-2xl bg-green-400 opacity-20 animate-pulse pointer-events-none"></div>
              )}
            </div>
          );
        })}
      </div>

      {/* Legend */}
      <div className="mt-6 flex justify-center gap-6 text-sm">
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 bg-green-200 border-2 border-green-500 rounded"></div>
          <span className="text-gray-700">Light ON</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 bg-gray-200 border-2 border-gray-500 rounded"></div>
          <span className="text-gray-700">Light OFF</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="bg-yellow-400 text-yellow-900 text-xs font-bold px-2 py-1 rounded">
            TARGET
          </div>
          <span className="text-gray-700">Target Area</span>
        </div>
      </div>
    </div>
  );
};

export default FloorPlanDisplay;
