import React, { useState, useEffect } from 'react';
import RoomLayout from './components/RoomLayout';
import './App.css';

function App() {
  const [rooms, setRooms] = useState([]);
  const [switchStates, setSwitchStates] = useState({});

  // Generate random number of rooms (4-8) and create room layout
  useEffect(() => {
    generateNewLayout();
  }, []);

  const generateNewLayout = () => {
    const numRooms = Math.floor(Math.random() * 5) + 4; // 4-8 rooms
    const newRooms = [];
    const newSwitchStates = {};

    // Generate irregular room shapes and positions
    for (let i = 0; i < numRooms; i++) {
      const roomId = String.fromCharCode(65 + i); // A, B, C, D, etc.

      // Create irregular room dimensions
      const width = Math.random() * 200 + 100; // 100-300px
      const height = Math.random() * 200 + 100; // 100-300px
      const left = Math.random() * 300; // 0-300px
      const top = Math.random() * 300; // 0-300px

      newRooms.push({
        id: roomId,
        label: roomId,
        isActive: false,
        width: width,
        height: height,
        left: left,
        top: top
      });
      newSwitchStates[roomId] = false;
    }

    setRooms(newRooms);
    setSwitchStates(newSwitchStates);
  };

  const toggleSwitch = (roomId) => {
    setSwitchStates(prev => ({
      ...prev,
      [roomId]: !prev[roomId]
    }));
  };

  return (
    <div className="app">
      <div className="app-header">
        <h1>Virtual Room Mock-up</h1>
        <button onClick={generateNewLayout} className="regenerate-btn">
          Generate New Layout
        </button>
      </div>

      <div className="app-content">
        <div className="layout-section">
          <h2>Floor Plan Layout</h2>
          <RoomLayout 
            rooms={rooms} 
            switchStates={switchStates}
            onToggleSwitch={toggleSwitch}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
