import React from 'react';
import './Room.css';

const Room = ({ room, isActive, onToggle }) => {
  const roomStyle = {
    position: 'absolute',
    left: `${room.left}px`,
    top: `${room.top}px`,
    width: `${room.width}px`,
    height: `${room.height}px`
  };

  const isLightArea = room.type === 'light';

  return (
    <div 
      className={`room ${isActive ? 'active' : ''} ${isLightArea ? 'light-area' : ''}`}
      style={roomStyle}
      onClick={onToggle}
    >
      {isLightArea ? (
        <div className="light-control">
          <div className="light-button">
            <div className="light-icon">ðŸ’¡</div>
            <span className="light-label">{room.label}</span>
          </div>
        </div>
      ) : (
        <div className="room-switch">
          <div className="switch-outer">
            <div className="switch-inner">
              <span className="room-label">{room.label}</span>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Room;
