import React, { useState, useEffect } from 'react';
import RoomLayout from './components/RoomLayout';
import STLViewer from './components/STLViewer';
import TestingControlPanel from './components/TestingControlPanel';

function App() {
  const [rooms, setRooms] = useState([]);
  const [switchStates, setSwitchStates] = useState({});
  const [selectedStlFile, setSelectedStlFile] = useState(null);

  // Testing state
  const [currentTrial, setCurrentTrial] = useState(1);
  const [isTrialActive, setIsTrialActive] = useState(false);
  const [currentSwitchType, setCurrentSwitchType] = useState('traditional'); // 'traditional' or 'roomLayout'
  const [currentOrientation, setCurrentOrientation] = useState('normal'); // 'normal' or 'rotated'
  const [targetSwitches, setTargetSwitches] = useState([]);
  const [participantData, setParticipantData] = useState({});
  const [isControlPanelCollapsed, setIsControlPanelCollapsed] = useState(false);

  // Generate random number of rooms (4-8) and create room layout
  useEffect(() => {
    generateNewLayout();
  }, []);

  const generateNewLayout = () => {
    const numRooms = Math.floor(Math.random() * 5) + 4; // 4-8 rooms
    const newRooms = [];
    const newSwitchStates = {};

    // Container dimensions
    const containerWidth = 800;
    const containerHeight = 600;

    // Generate creative floor plan with only rooms (no light areas)
    const rooms = generateCreativeFloorPlan(numRooms, containerWidth, containerHeight);

    rooms.forEach((room, i) => {
      const roomId = String.fromCharCode(65 + i); // A, B, C, D, etc.
      newRooms.push({
        id: roomId,
        label: roomId,
        isActive: false,
        width: room.width,
        height: room.height,
        left: room.left,
        top: room.top,
        type: 'room'
      });
      newSwitchStates[roomId] = false;
    });

    setRooms(newRooms);
    setSwitchStates(newSwitchStates);

    // Generate random targets for this layout
    generateRandomTargets(newRooms);
  };

  // Generate random target switches for the trial
  const generateRandomTargets = (roomList) => {
    const numTargets = Math.floor(Math.random() * 3) + 2; // 2-4 targets
    const shuffled = [...roomList].sort(() => 0.5 - Math.random());
    const targets = shuffled.slice(0, numTargets).map(room => room.id);
    setTargetSwitches(targets);
  };

  // Randomize switch type and orientation
  const randomizeTrialSettings = () => {
    const switchTypes = ['traditional', 'roomLayout'];
    const orientations = ['normal', 'rotated'];

    setCurrentSwitchType(switchTypes[Math.floor(Math.random() * switchTypes.length)]);
    setCurrentOrientation(orientations[Math.floor(Math.random() * orientations.length)]);

    // Also randomize STL file selection
    const stlFiles = [
      { id: 1, name: 'Room 1 (3).stl', description: 'Room Layout 1' },
      { id: 2, name: 'Room 2 (4).stl', description: 'Room Layout 2' },
      { id: 3, name: 'Room 3 (5).stl', description: 'Room Layout 3' },
      { id: 4, name: 'Room 4 (6).stl', description: 'Room Layout 4' },
      { id: 5, name: 'Room 5 (3).stl', description: 'Room Layout 5' },
      { id: 6, name: 'Room 6 (6).stl', description: 'Room Layout 6' }
    ];

    const randomStl = stlFiles[Math.floor(Math.random() * stlFiles.length)];
    setSelectedStlFile(randomStl.id);
  };

  // Function to generate creative floor plan with only rooms
  const generateCreativeFloorPlan = (numRooms, containerWidth, containerHeight) => {
    const rooms = [];
    const margin = 0; // No spacing between rooms

    // Create more organic, realistic floor plans with no spacing
    const layouts = [
      // L-shaped layout
      () => {
        const room1 = { width: 300, height: 200, left: margin, top: margin };
        const room2 = { width: 250, height: 200, left: room1.width, top: margin };
        const room3 = { width: 300, height: 200, left: margin, top: room1.height };
        const room4 = { width: 250, height: 200, left: room3.width, top: room2.height };

        return [room1, room2, room3, room4];
      },

      // Grid layout
      () => {
        const room1 = { width: 200, height: 150, left: margin, top: margin };
        const room2 = { width: 200, height: 150, left: room1.width, top: margin };
        const room3 = { width: 200, height: 150, left: room2.width, top: margin };
        const room4 = { width: 200, height: 150, left: margin, top: room1.height };
        const room5 = { width: 200, height: 150, left: room4.width, top: room2.height };
        const room6 = { width: 200, height: 150, left: room5.width, top: room3.height };

        return [room1, room2, room3, room4, room5, room6];
      },

      // Scattered layout
      () => {
        const room1 = { width: 220, height: 140, left: margin, top: margin };
        const room2 = { width: 180, height: 160, left: room1.width, top: margin };
        const room3 = { width: 240, height: 120, left: margin, top: room1.height };
        const room4 = { width: 160, height: 140, left: room3.width, top: room2.height };
        const room5 = { width: 200, height: 130, left: room4.width + room3.width, top: room2.height };

        return [room1, room2, room3, room4, room5];
      },

      // U-shaped layout
      () => {
        const room1 = { width: 200, height: 250, left: margin, top: margin };
        const room2 = { width: 300, height: 120, left: room1.width, top: margin };
        const room3 = { width: 200, height: 250, left: room2.width + room1.width, top: margin };
        const room4 = { width: 200, height: 200, left: margin, top: room1.height };
        const room5 = { width: 200, height: 200, left: room4.width + room2.width, top: room2.height };

        return [room1, room2, room3, room4, room5];
      }
    ];

    // Select a random layout
    const selectedLayout = layouts[Math.floor(Math.random() * layouts.length)];
    return selectedLayout();
  };

  const toggleSwitch = (roomId) => {
    setSwitchStates(prev => ({
      ...prev,
      [roomId]: !prev[roomId]
    }));
  };

  // Trial management functions
  const handleStartTrial = () => {
    setIsTrialActive(true);
    randomizeTrialSettings();
    generateNewLayout();
  };

  const handleNextTrial = () => {
    setIsTrialActive(false);
    setCurrentTrial(prev => prev + 1);
    // Reset all switches
    const resetStates = {};
    rooms.forEach(room => {
      resetStates[room.id] = false;
    });
    setSwitchStates(resetStates);
  };

  const handleSwitchPress = (roomId) => {
    toggleSwitch(roomId);
  };

  const handleUpdateParticipantData = (data) => {
    setParticipantData(data);
  };

  const handleToggleControlPanel = () => {
    setIsControlPanelCollapsed(prev => !prev);
  };

  const handleNextLayout = () => {
    randomizeTrialSettings();
    generateNewLayout();
  };

  // STL files from the public/stl folder
  const stlFiles = [
    { id: 1, name: 'Room 1 (3).stl', description: 'Room Layout 1' },
    { id: 2, name: 'Room 2 (4).stl', description: 'Room Layout 2' },
    { id: 3, name: 'Room 3 (5).stl', description: 'Room Layout 3' },
    { id: 4, name: 'Room 4 (6).stl', description: 'Room Layout 4' },
    { id: 5, name: 'Room 5 (3).stl', description: 'Room Layout 5' },
    { id: 6, name: 'Room 6 (6).stl', description: 'Room Layout 6' }
  ];

  return (
    <div className="min-h-screen bg-gray-100 p-5">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="bg-gradient-to-r from-indigo-500 to-purple-600 text-white p-5 rounded-xl shadow-lg mb-6">
          <h1 className="text-2xl font-semibold text-center">Switch Testing Application</h1>
          <p className="text-center text-indigo-100 mt-2">
            Testing Traditional vs Room Layout Switch Interfaces
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Control Panel */}
          <div className="lg:col-span-1">
            <TestingControlPanel
              onStartTrial={handleStartTrial}
              onNextTrial={handleNextTrial}
              onSwitchPress={handleSwitchPress}
              currentTrial={currentTrial}
              participantData={participantData}
              onUpdateParticipantData={handleUpdateParticipantData}
              isTrialActive={isTrialActive}
              targetSwitches={targetSwitches}
              switchStates={switchStates}
              isCollapsed={isControlPanelCollapsed}
              onToggleCollapse={handleToggleControlPanel}
            />
          </div>

          {/* Testing Interface */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-xl shadow-lg overflow-hidden">
              {/* Trial Info Header */}
              <div className="bg-gray-50 p-4 border-b">
                <div className="flex justify-between items-center">
                  <div>
                    <h2 className="text-lg font-semibold text-gray-800">
                      {currentSwitchType === 'traditional' ? 'Traditional Switch Panel' : 'Room Layout Interface'}
                    </h2>
                    <p className="text-sm text-gray-600">
                      Trial {currentTrial} â€¢ Orientation: {currentOrientation}
                    </p>
                  </div>
                  <div className="text-sm text-gray-600">
                    {isTrialActive ? (
                      <span className="text-green-600 font-medium">Trial Active</span>
                    ) : (
                      <span className="text-gray-500">Trial Inactive</span>
                    )}
                  </div>
                </div>
              </div>

              {/* Switch Interface */}
              <div className="p-6">
                {currentSwitchType === 'traditional' ? (
                  <div className="w-full">
                    <h3 className="mb-4 text-gray-800 text-lg font-semibold text-center">Traditional Light Switch Panel</h3>
                    <div className="flex gap-5 p-5 bg-gray-50 rounded-xl border-2 border-gray-200 justify-center items-center">
                      {rooms.map((room) => (
                        <div 
                          key={room.id} 
                          className="w-8 h-16 bg-gray-200 rounded-2xl relative cursor-pointer transition-all duration-300 border-2 border-gray-300 hover:scale-105"
                          onClick={() => handleSwitchPress(room.id)}
                        >
                          <div className={`w-6 h-6 bg-white rounded-full absolute left-0.5 top-0.5 transition-all duration-300 shadow-md ${
                            switchStates[room.id] ? 'translate-y-0' : 'translate-y-7'
                          }`}></div>
                        </div>
                      ))}
                    </div>
                  </div>
                ) : (
                  <div className="w-full">
                    <h3 className="mb-4 text-gray-800 text-lg font-semibold text-center">Room Layout Interface</h3>
                    <div className={`transform transition-transform duration-500 ${
                      currentOrientation === 'rotated' ? 'rotate-180' : ''
                    }`}>
                      <RoomLayout 
                        rooms={rooms} 
                        switchStates={switchStates}
                        onToggleSwitch={handleSwitchPress}
                      />
                    </div>
                  </div>
                )}
              </div>

              {/* STL Viewer (if room layout is selected) */}
              {currentSwitchType === 'roomLayout' && (
                <div className="border-t p-6 bg-gray-50">
                  <h3 className="mb-4 text-gray-800 text-lg font-semibold text-center">3D Room Layout Reference</h3>
                  <STLViewer 
                    stlFile={selectedStlFile ? stlFiles.find(f => f.id === selectedStlFile) : null}
                    isVisible={true}
                  />
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
