import React from 'react';
import { useExperiment } from '../context/ExperimentContext';

const GroundTruthFloorPlan = () => {
  const { targetAreas, lightsToTurnOff, roomLayout, floorPlanOrientation, lightStates, currentCondition } = useExperiment();

  // Use room layout if available, otherwise default to 4 rooms
  const rooms = roomLayout?.rooms || [
    { id: 'A', left: 0, top: 0, width: 400, height: 300, name: 'Office A' },
    { id: 'B', left: 400, top: 0, width: 400, height: 300, name: 'Office B' },
    { id: 'C', left: 0, top: 300, width: 533, height: 300, name: 'Conference Room' },
    { id: 'D', left: 533, top: 300, width: 267, height: 300, name: 'Kitchen' }
  ];

  const taskType = currentCondition?.taskType || 'turnOn2';

  return (
    <div className="w-full">
      {/* Task Instructions */}
      <div className="mb-4 text-center">
        {(() => {
          // Get room names for target areas and lights to turn off
          const getRoomName = (areaId) => {
            const room = rooms.find(r => r.id === areaId);
            return room?.name || areaId;
          };

          const targetRoomNames = targetAreas.map(getRoomName);
          const turnOffRoomNames = lightsToTurnOff.map(getRoomName);

          if (taskType === 'turnOn2') {
            return (
              <>
                <p className="text-lg font-semibold text-gray-700 mb-2">
                  Task: Turn ON 2 lights
                </p>
                <p className="text-sm text-gray-600">
                  Turn ON lights for: <span className="font-bold text-yellow-600">{targetRoomNames.length > 0 ? targetRoomNames.join(', ') : 'None'}</span>
                </p>
                <p className="text-xs text-gray-500 mt-1">
                  (Areas: {targetAreas.join(', ')})
                </p>
              </>
            );
          } else if (taskType === 'turnOn1Off1') {
            return (
              <>
                <p className="text-lg font-semibold text-gray-700 mb-2">
                  Task: Turn ON 1 light, Turn OFF 1 light
                </p>
                <p className="text-sm text-gray-600">
                  Turn ON: <span className="font-bold text-yellow-600">{targetRoomNames.length > 0 ? targetRoomNames.join(', ') : 'None'}</span>
                  {turnOffRoomNames.length > 0 && (
                    <> | Turn OFF: <span className="font-bold text-red-600">{turnOffRoomNames.join(', ')}</span></>
                  )}
                </p>
                <p className="text-xs text-gray-500 mt-1">
                  (Turn ON: {targetAreas.join(', ')}, Turn OFF: {lightsToTurnOff.join(', ')})
                </p>
              </>
            );
          } else {
            return (
              <>
                <p className="text-lg font-semibold text-gray-700 mb-2">
                  Target Areas: <span className="text-yellow-600 font-bold">{targetRoomNames.length > 0 ? targetRoomNames.join(', ') : 'None'}</span>
                </p>
                <p className="text-sm text-gray-600">
                  Turn ON the lights for these areas
                </p>
              </>
            );
          }
        })()}
      </div>

      {/* Floor Plan Container - with rotation */}
      {/* Container needs to be large enough to accommodate rotated floor plan */}
      <div className="max-w-5xl mx-auto" style={{ 
        minHeight: '800px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '20px'
      }}>
        <div 
          className="relative bg-white rounded-lg shadow-lg"
          style={{ 
            width: '800px',
            height: '600px',
            maxWidth: 'calc(100% - 40px)',
            maxHeight: 'calc(100% - 40px)',
            transform: `rotate(${floorPlanOrientation || 0}deg)`,
            transformOrigin: 'center center',
            backgroundImage: 'repeating-linear-gradient(0deg, transparent, transparent 19px, #e5e7eb 19px, #e5e7eb 20px), repeating-linear-gradient(90deg, transparent, transparent 19px, #e5e7eb 19px, #e5e7eb 20px)',
            backgroundSize: '20px 20px',
            border: '4px solid #000000'
          }}
        >
          {/* Render rooms from layout */}
          {rooms.map((room, index) => {
            const isOn = lightStates[room.id] === true; // Show yellow when switch is turned on
            const roomName = room.name || `Room ${room.id}`;

            // Check if there's a room to the right, left, above, or below
            const hasRoomRight = rooms.some(r => 
              r.id !== room.id &&
              Math.abs(r.top - room.top) < 10 && 
              Math.abs(r.left - (room.left + room.width)) < 10
            );
            const hasRoomBelow = rooms.some(r => 
              r.id !== room.id &&
              Math.abs(r.left - room.left) < 10 && 
              Math.abs(r.top - (room.top + room.height)) < 10
            );
            const hasRoomLeft = rooms.some(r => 
              r.id !== room.id &&
              Math.abs(r.top - room.top) < 10 && 
              Math.abs((r.left + r.width) - room.left) < 10
            );
            const hasRoomAbove = rooms.some(r => 
              r.id !== room.id &&
              Math.abs(r.left - room.left) < 10 && 
              Math.abs((r.top + r.height) - room.top) < 10
            );

            // Also check if this room is on the edge
            const isLeftEdge = room.left === 0;
            const isTopEdge = room.top === 0;
            const isRightEdge = Math.abs((room.left + room.width) - 800) < 10;
            const isBottomEdge = Math.abs((room.top + room.height) - 600) < 10;

            // Determine room type for furniture
            const roomNameLower = roomName.toLowerCase();
            const isOffice = roomNameLower.includes('office');
            const isConference = roomNameLower.includes('conference') || roomNameLower.includes('meeting') || roomNameLower.includes('board');
            const isKitchen = roomNameLower.includes('kitchen') || roomNameLower.includes('break');
            const isBathroom = roomNameLower.includes('bathroom') || roomNameLower.includes('restroom') || roomNameLower.includes('men\'s room') || roomNameLower.includes('women\'s room');
            const isStorage = roomNameLower.includes('storage') || roomNameLower.includes('closet') || roomNameLower.includes('supply');
            const isReception = roomNameLower.includes('reception') || roomNameLower.includes('lobby');
            const isChangeroom = roomNameLower.includes('changeroom') || roomNameLower.includes('locker');

            return (
              <div
                key={room.id}
                className="absolute transition-all duration-300"
                style={{
                  left: `${(room.left / 800) * 100}%`,
                  top: `${(room.top / 600) * 100}%`,
                  width: `${(room.width / 800) * 100}%`,
                  height: `${(room.height / 600) * 100}%`,
                  // Show borders between rooms and on edges (but not on outer container edges)
                  borderRight: (hasRoomRight || isRightEdge) ? '3px solid #000000' : 'none',
                  borderBottom: (hasRoomBelow || isBottomEdge) ? '3px solid #000000' : 'none',
                  borderLeft: (hasRoomLeft || isLeftEdge) ? 'none' : 'none',
                  borderTop: (hasRoomAbove || isTopEdge) ? 'none' : 'none',
                  backgroundColor: isOn ? 'rgba(253, 224, 71, 0.15)' : '#f9fafb',
                  boxSizing: 'border-box',
                  zIndex: 1
                }}
              >
                <div className="w-full h-full relative p-2">
                  {/* Room name label */}
                  <div className="absolute top-1 left-1 z-10">
                    <span className={`text-xs font-semibold px-2 py-1 rounded shadow-sm ${
                      isOn 
                        ? 'bg-yellow-200 text-yellow-800 border border-yellow-400' 
                        : 'bg-white text-gray-700 border border-gray-300'
                    }`}>
                      {roomName}
                    </span>
                  </div>

                  {/* Furniture based on room type */}
                  <div className="absolute inset-0 flex items-center justify-center" style={{ paddingTop: '28px', paddingLeft: '4px', paddingRight: '4px' }}>
                    {isOffice && (
                      <div className="flex flex-wrap items-center justify-center gap-2" style={{ transform: 'scale(0.75)' }}>
                        {/* Desk */}
                        <div className="w-12 h-8 bg-gray-400 border border-gray-600 rounded-sm shadow-sm"></div>
                        {/* Chair */}
                        <div className="w-6 h-6 bg-gray-500 border border-gray-700 rounded-full shadow-sm"></div>
                        {/* Another desk/chair set if space allows */}
                        {(room.width > 200 || room.height > 150) && (
                          <>
                            <div className="w-12 h-8 bg-gray-400 border border-gray-600 rounded-sm shadow-sm"></div>
                            <div className="w-6 h-6 bg-gray-500 border border-gray-700 rounded-full shadow-sm"></div>
                          </>
                        )}
                      </div>
                    )}
                    {isConference && (
                      <div className="flex flex-col items-center justify-center gap-2" style={{ transform: 'scale(0.85)' }}>
                        {/* Conference table */}
                        <div className="w-24 h-16 bg-amber-800 border-2 border-amber-900 rounded-lg shadow-md"></div>
                        {/* Chairs around table */}
                        <div className="flex gap-4">
                          <div className="w-5 h-5 bg-gray-500 border border-gray-700 rounded-full shadow-sm"></div>
                          <div className="w-5 h-5 bg-gray-500 border border-gray-700 rounded-full shadow-sm"></div>
                          <div className="w-5 h-5 bg-gray-500 border border-gray-700 rounded-full shadow-sm"></div>
                          <div className="w-5 h-5 bg-gray-500 border border-gray-700 rounded-full shadow-sm"></div>
                        </div>
                      </div>
                    )}
                    {isKitchen && (
                      <div className="flex flex-col items-center justify-center gap-2" style={{ transform: 'scale(0.75)' }}>
                        {/* Counter */}
                        <div className="w-20 h-6 bg-gray-300 border-2 border-gray-500 rounded shadow-sm"></div>
                        {/* Sink */}
                        <div className="w-8 h-8 bg-blue-200 border-2 border-blue-400 rounded-full shadow-sm"></div>
                        {/* Cabinet */}
                        <div className="w-16 h-4 bg-gray-400 border border-gray-600 rounded shadow-sm"></div>
                      </div>
                    )}
                    {isBathroom && (
                      <div className="flex items-center justify-center gap-3" style={{ transform: 'scale(0.85)' }}>
                        {/* Toilet */}
                        <div className="w-6 h-8 bg-white border-2 border-gray-600 rounded-b-lg shadow-sm"></div>
                        {/* Sink */}
                        <div className="w-6 h-6 bg-blue-200 border-2 border-blue-400 rounded-full shadow-sm"></div>
                      </div>
                    )}
                    {isStorage && (
                      <div className="flex items-center justify-center" style={{ transform: 'scale(0.75)' }}>
                        {/* Shelves */}
                        <div className="flex flex-col gap-1">
                          <div className="w-16 h-2 bg-gray-400 border border-gray-600 shadow-sm"></div>
                          <div className="w-16 h-2 bg-gray-400 border border-gray-600 shadow-sm"></div>
                          <div className="w-16 h-2 bg-gray-400 border border-gray-600 shadow-sm"></div>
                        </div>
                      </div>
                    )}
                    {isReception && (
                      <div className="flex items-center justify-center gap-2" style={{ transform: 'scale(0.8)' }}>
                        {/* Reception desk */}
                        <div className="w-20 h-10 bg-gray-400 border-2 border-gray-600 rounded shadow-sm"></div>
                        {/* Chair */}
                        <div className="w-6 h-6 bg-gray-500 border border-gray-700 rounded-full shadow-sm"></div>
                      </div>
                    )}
                    {isChangeroom && (
                      <div className="flex items-center justify-center gap-2" style={{ transform: 'scale(0.8)' }}>
                        {/* Lockers */}
                        <div className="flex flex-col gap-1">
                          <div className="w-8 h-6 bg-gray-400 border border-gray-600 shadow-sm"></div>
                          <div className="w-8 h-6 bg-gray-400 border border-gray-600 shadow-sm"></div>
                        </div>
                        <div className="flex flex-col gap-1">
                          <div className="w-8 h-6 bg-gray-400 border border-gray-600 shadow-sm"></div>
                          <div className="w-8 h-6 bg-gray-400 border border-gray-600 shadow-sm"></div>
                        </div>
                      </div>
                    )}
                    {!isOffice && !isConference && !isKitchen && !isBathroom && !isStorage && !isReception && !isChangeroom && (
                      <div className="flex items-center justify-center gap-2" style={{ transform: 'scale(0.75)' }}>
                        {/* Generic furniture */}
                        <div className="w-10 h-8 bg-gray-400 border border-gray-600 rounded-sm shadow-sm"></div>
                        <div className="w-5 h-5 bg-gray-500 border border-gray-700 rounded-full shadow-sm"></div>
                      </div>
                    )}
                  </div>

                  {/* Highlight overlay when light is on */}
                  {isOn && (
                    <div className="absolute inset-0 bg-yellow-200/30 rounded-lg pointer-events-none border-2 border-yellow-500"></div>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Legend */}
      <div className="mt-6 flex justify-center gap-6 text-sm flex-wrap">
        <div className="flex items-center gap-2">
          <div className="w-5 h-5 rounded-full border-4 border-yellow-500 bg-yellow-200"></div>
          <span className="text-gray-700 font-semibold">Light ON</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-5 h-5 rounded-full border-4 border-black bg-white"></div>
          <span className="text-gray-700">Light OFF</span>
        </div>
      </div>

      {/* Orientation indicator */}
      {floorPlanOrientation && floorPlanOrientation !== 0 && (
        <div className="mt-4 text-center text-sm text-gray-600">
          Orientation: {floorPlanOrientation}Â°
        </div>
      )}
    </div>
  );
};

export default GroundTruthFloorPlan;
