import React from 'react';
import Room from './Room';
import './RoomLayout.css';

const RoomLayout = ({ rooms, switchStates, onToggleSwitch }) => {
  // Calculate grid layout based on number of rooms
  const getGridLayout = (roomCount) => {
    if (roomCount <= 4) {
      return {
        gridTemplateColumns: '1fr 1fr',
        gridTemplateRows: '1fr 1fr'
      };
    } else if (roomCount <= 6) {
      return {
        gridTemplateColumns: '1fr 1fr 1fr',
        gridTemplateRows: '1fr 1fr'
      };
    } else {
      return {
        gridTemplateColumns: '1fr 1fr 1fr 1fr',
        gridTemplateRows: '1fr 1fr'
      };
    }
  };

  const gridStyle = getGridLayout(rooms.length);

  return (
    <div className="room-layout">
      <div 
        className="room-grid" 
        style={gridStyle}
      >
        {rooms.map((room) => (
          <Room
            key={room.id}
            room={room}
            isActive={switchStates[room.id]}
            onToggle={() => onToggleSwitch(room.id)}
          />
        ))}
      </div>
    </div>
  );
};

export default RoomLayout;
